INSERT INTO `wp_wc_admin_notes` (`note_id`, `name`, `type`, `locale`, `title`, `content`, `icon`, `content_data`, `status`, `source`, `date_created`, `date_reminder`, `is_snoozable`) VALUES
(1,	'wc-admin-historical-data',	'update',	'en_US',	'WooCommerce Admin: Historical Analytics Data',	'To view your historical analytics data, you must process your existing orders and customers.',	'info',	'{}',	'unactioned',	'woocommerce-admin',	'2020-03-30 19:41:28',	NULL,	0),
(2,	'wc-admin-welcome-note',	'info',	'en_US',	'New feature(s)',	'Welcome to the new WooCommerce experience! In this new release you\'ll be able to have a glimpse of how your store is doing in the Dashboard, manage important aspects of your business (such as managing orders, stock, reviews) from anywhere in the interface, dive into your store data with a completely new Analytics section and more!',	'info',	'{}',	'unactioned',	'woocommerce-admin',	'2020-03-30 19:41:28',	NULL,	0),
(3,	'wc-admin-store-notice-setting-moved',	'info',	'en_US',	'Looking for the Store Notice setting?',	'It can now be found in the Customizer.',	'info',	'{}',	'unactioned',	'woocommerce-admin',	'2020-03-30 19:41:28',	NULL,	0),
(4,	'wc-admin-orders-milestone',	'info',	'en_US',	'Congratulations on processing 10 orders!',	'You\'ve hit the 10 orders milestone! Look at you go. Browse some WooCommerce success stories for inspiration.',	'trophy',	'{}',	'unactioned',	'woocommerce-admin',	'2020-03-30 19:41:28',	NULL,	0),
(5,	'wc-admin-wc-helper-connection',	'info',	'en_US',	'Connect to WooCommerce.com',	'Connect to get important product notifications and updates.',	'info',	'{}',	'unactioned',	'woocommerce-admin',	'2020-03-30 19:41:28',	NULL,	0),
(6,	'wc-update-db-reminder',	'update',	'en_US',	'WooCommerce database update done',	'WooCommerce database update complete. Thank you for updating to the latest version!',	'info',	'{}',	'actioned',	'woocommerce-core',	'2020-03-30 19:41:40',	NULL,	0),
(7,	'wc-admin-mobile-app',	'info',	'en_US',	'Install Woo mobile app',	'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics ‚Äî wherever you are.',	'phone',	'{}',	'unactioned',	'woocommerce-admin',	'2020-04-02 12:22:34',	NULL,	0),
(8,	'wc-admin-onboarding-email-marketing',	'info',	'en_US',	'Tips, product updates, and inspiration',	'We\'re here for you ‚Äî get tips, product updates, and inspiration straight to your mailbox.',	'mail',	'{}',	'unactioned',	'woocommerce-admin',	'2020-04-02 12:22:34',	NULL,	0),
(9,	'wc-admin-store-notice-giving-feedback',	'info',	'en_US',	'Review your experience',	'If you like WooCommerce Admin please leave us a 5 star rating. A huge thanks in advance!',	'info',	'{}',	'unactioned',	'woocommerce-admin',	'2020-04-13 21:02:44',	NULL,	0),
(10,	'wc-admin-facebook-extension',	'info',	'en_US',	'Market on Facebook',	'Grow your business by targeting the right people and driving sales with Facebook. You can install this free extension now.',	'thumbs-up',	'{}',	'unactioned',	'woocommerce-admin',	'2020-04-13 21:02:44',	NULL,	0),
(11,	'wc-admin-usage-tracking-opt-in',	'info',	'en_US',	'Help WooCommerce improve with usage tracking',	'Gathering usage data allows us to improve WooCommerce. Your store will be considered as we evaluate new features, judge the quality of an update, or determine if an improvement makes sense. You can always visit the <a href=\"https://wc4bp.local/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Settings</a> and choose to stop sharing data. <a href=\"https://woocommerce.com/usage-tracking\" target=\"_blank\">Read more</a> about what data we collect.',	'info',	'{}',	'unactioned',	'woocommerce-admin',	'2020-04-13 21:02:44',	NULL,	0);
